import { useState } from 'react';
import Banner from './componetes/Banner/Banner';
import Formulario from './componetes/Formulario';
import Time from './componetes/Time';

function App() {

  const times = [
    {
      nome: 'Front-End',
      corPrimaria: '#57C278',
      corSecundaria: '#D9F7E9'
    },
    {
      nome: 'Direito',
      corPrimaria: '#82CFFA',
      corSecundaria: '#E8F8FF'
    },
    {
      nome: 'Contabilidade',
      corPrimaria: '#A6D157',
      corSecundaria: '#F0F8E2'
    },
    {
      nome: 'Vendas',
      corPrimaria: '#E06B69',
      corSecundaria: '#FDE7E8'
    }
  ]

  const [colaboradores, setColaboradores] = useState([])
  const aoNovoColaboradoAdicionado = (colaborador) => {
    debugger
    setColaboradores([...colaboradores, colaborador])

  }
  return (
    <div className="App">
      <Banner />
      <Formulario times={times.map(time => time.nome)} aoColadoradorCadastrado={colaborador => aoNovoColaboradoAdicionado(colaborador)} />
      {times.map(time => <Time
        key={time.nome}
        nome={time.nome}
        corPrimaria={time.corPrimaria}
        corSecundaria={time.corSecundaria}
        colaboradores = {colaboradores.filter(colaborador => colaborador.time === time.nome)}
      />)}


    </div>
  );
}

export default App;
